import React from "react";
import { useCart } from "react-use-cart";

const Carts = () => {
  const {
    isEmpty,
    totalUniqueItems,
    items,
    totalItems,
    cartTotal,
    updateItemQuantity,
    removeItem,
    emptyCart,
  } = useCart();

  if (isEmpty) return <h5 className="text-center py-5">My Cart is Empty</h5>;

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
            <p>Cart <span className="badge bd-danger">0</span></p>
          <p>Cart ({totalUniqueItems}) total items ({totalItems})</p>
        </div>
      </div>
    </div>
  );
};

export default Carts;
